fill((2,0)--(4,2)--(4,0)--cycle,gray(.7));
draw((0,0)--(0,5)--(5,5)--(5,0)--cycle,linewidth(1));
draw((0,0)--(5,5),linewidth(1));
draw((1,1)--(2,0)--(5,2),linewidth(1));
draw((0,5)--(3,1)--(3,3),linewidth(1));
label("$E$",(0,5),W);
label("$F$",(5,5),E);
label("$G$",(5,0),E);
label("$H$",(0,0),W);
label("f",(2,1));

[/asy]

