pair F = dir(360*4/8+90);
pair C = A+dir(90)*(F-A);
pair D = C+F-A;
pair B = dir(360*2/8+90);

draw(A--C--D--F);

label("$A$",A,S);
label("$B$",B,W);
label("$C$",C,SE);
label("$D$",F,S);

[/asy]

